<script>
	import { onMount } from "svelte";
	import StaffPanel from "./StaffPanel.svelte";

	let currentUser = {
		name: "น้องแทน",
		studentID: "123456789",
		points: 0,
		redeemed: [],
	};

	let showStaffPanel = false;

	const maxPoints = 10000;

	const milestones = [
		{
			points: 2000,
			reward: "ดินสอ",
			image: "data:image/jpeg;base64,",
			id: "pencil",
		},
		{
			points: 4000,
			reward: "สมุดโน้ต",
			image: "data:image/jpeg;base64,",
			id: "notebook",
		},
		{
			points: 6000,
			reward: "กระเป๋า",
			image: "data:image/jpeg;base64,",
			id: "bag",
		},
		{
			points: 8000,
			reward: "เสื้อยืด",
			image: "data:image/jpeg;base64,",
			id: "tshirt",
		},
		{
			points: 9000,
			reward: "แท็บเล็ต",
			image: "data:image/jpeg;base64,",
			id: "tablet",
		},
	];
	//มี id อยู่ด้านหลังไกลๆใน milestones
	function toggleStaffPanel() {
		showStaffPanel = !showStaffPanel;
	}

	function updatePoints(amount) {
		currentUser.points += amount;
	}

	function closeStaffPanel() {
		showStaffPanel = false;
	}

	// หาของรางวัลชิ้นถัดไปที่ยังไม่ได้รับ
	$: nextReward = milestones.find((m) => m.points > currentUser.points) || false;
	$: pointsToNextReward = nextReward ? nextReward.points - currentUser.points : 0;
</script>

<svelte:head>
	<title>ระบบสะสมคะแนน</title>
</svelte:head>

<div class="container">
	<div class="next-reward-info">
		{#if nextReward}
			<h3>อีก {pointsToNextReward} คะแนน จะได้รับ {nextReward.reward}!</h3>
		{:else}
			<p>เก็บรางวัลครบทั้งหมดแล้ว</p>
		{/if}
	</div>

	<!-- ส่วนนี้สำหรับการทดลองเพิ่มคะแนน -->
	<div class="test-controls">
		<button on:click={() => updatePoints(100)}>เพิ่ม 100 คะแนน</button>
		<button on:click={() => updatePoints(500)}>เพิ่ม 500 คะแนน</button>
		<button on:click={() => updatePoints(1000)}>เพิ่ม 1000 คะแนน</button>
	</div>

	<div class="staff-access">
		<button on:click={toggleStaffPanel}>
			{showStaffPanel ? "ปิดหน้าจอสตาฟ" : "เข้าสู่หน้าจอสตาฟ"}
		</button>
	</div>

	{#if showStaffPanel}
		<div class="overlay" on:click={closeStaffPanel}>
			<div on:click|stopPropagation>
				<StaffPanel
					{currentUser}
					{milestones}
					on:updatePoints={(e) => updatePoints(e.detail.amount)}
					on:close={() => closeStaffPanel()}
				/>
			</div>
		</div>
	{/if}
</div>

